import React from 'react'
import ReactModal from 'react-modal'

class EditModal extends React.Component {
  constructor() {
    super();
    this.state = {
      title: '',
      description: '',
      isOpen: false

    };
  }

  handleChange = (event) => {
      this.setState({
          [event.target.name]: event.target.value
      })
  }

  handleOpenModal = () => {
      this.setState({isOpen: true})
  }
  handleCloseModal = () => {
    this.setState({isOpen: false})
  }

  handleSubmit = () => {
      //event.preventDefault()

      const {Â title, description, isOpen } = this.state
      if (title && description) {
        var myHeaders = new Headers();

        var body = {
            title: title,
            description: description
        }


        var params = { method: 'POST',
                       headers: myHeaders,
                       mode: 'cors',
                       cache: 'default',
                       body: JSON.stringify(body)
                     };
        fetch('https://api.myidea.fr/v1/notes', params)
        .then(response => console.log(response))
        .catch(error => console.error(error))

      }
  }



  render() {
      const { title, description, isOpen } = this.state
    return (
        <div>
            <button onClick={this.handleOpenModal}>OPEN MODAL</button>
            <ReactModal
                isOpen={isOpen}
            >
                <form onSubmit={this.handleSubmit}>
                    <label>Titre : </label>
                    <input name='title' onChange={this.handleChange} value={title} />
                    <label>Description : </label>
                    <input name='description' onChange={this.handleChange} value={description} />
                    <button type='submit'>AJOUTER</button>
                </form>
                <button onClick={this.handleCloseModal}>CLOSE MODAL</button>




        </ReactModal>
        </div>


    )
  }


}

export default EditModal;
