import React from 'react'
import ReactModal from 'react-modal'

class EditModal extends React.Component {
  constructor() {
    super();
    this.state = {
      title: '',
      description: '',
      isOpen: false

    };
  }

  handleChange = (event) => {
      this.setState({
          [event.target.name]: event.target.value
      })
  }

  handleOpenModal = () => {
      this.setState({isOpen: true})
  }
  handleCloseModal = () => {
    this.setState({isOpen: false})
  }

  handleSubmit = () => {
      const {Â title, description, isOpen } = this.state
      if (title && description) {
        var myHeaders = new Headers();

        var myInit = { method: 'POST',
                       headers: myHeaders,
                       mode: 'cors',
                       cache: 'default' };


      }
  }



  render() {
      const { title, description, isOpen } = this.state
    return (
        <div>
            <button onClick={this.handleOpenModal}>OPEN MODAL</button>
            <ReactModal
                isOpen={isOpen}
            >
                <form onSubmit={this.handleSubmit}>
                    <label>Titre : </label>
                    <input name='title' onChange={this.handleChange} value={title} />
                    <label>Description : </label>
                    <input name='descrition' onChange={this.handleChange} value={description} />
                </form>
                <button onClick={this.handleCloseModal}>CLOSE MODAL</button>
                <button type='submit'>AJOUTER</button>



        </ReactModal>
        </div>


    )
  }


}

export default EditModal;
