import React from 'react'
import Draggable from 'react-draggable'
import './styles/NoteStyle.css'

const URL = 'https://api.myidea.fr/v1/notes/'

class Note extends React.Component {


  getRandomColor () {
    var letters = '0123456789ABCDEF'
    var color = '#'
    for (var i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)]
    }
    return color
  }




  
 componentDidMount() {
  const { note } = this.props
   
   let test = note.id
   console.log(test)
 } 

  handleDelete () {
    console.log(URL + test)
    const { note } = this.props
   console.log(note.id)
    
    return new Promise((resolve, reject) => {
      window.fetch(URL + test )
      .then(response => response.json())
      .then(result => {
          console.log('Réponse API getNotes', result)
          resolve(result)
      })
      .catch(error => {
          console.error(error)
          reject(error)
          })
      })

  }



  render () {
    const { note } = this.props
    return (
      note
        ? (
          <Draggable>
            <div className='note' style={{ backgroundColor: this.getRandomColor() }}>
              <button onClick={this.handleDelete} style={{ width: 20, marginLeft: 80 }}>
                X
              </button>
              <span className='title'>
                <h2>{note.title}</h2>
              </span>
              <div className='separator' />
              <span className='description'>
                {note.description}
              </span>
            </div>
          </Draggable>
        )
        : 'NO DATA'
    )
  }
}

export default Note
