const URL = "https://api.myidea.fr/v1/"

export function getNotes () {
    return new Promise((resolve, reject) => {
    window.fetch(URL + 'notes')
    .then(response => response.json())
    .then(result => {
        console.log('Réponse API getNotes', result)
        resolve(result)
    })
    .catch(error => {
        console.error(error)
        reject(error)
        })
    })
}
